import * as fs from 'fs';
import DataBaseEntry from '../Types/DataBaseEntry';

const url = 'http://localhost:5001/api/database';

export function readDb(dbName: string){
    const data = fs.readFileSync(dbName, 'utf-8');
    return data;
}

export function writeDb(obj: DataBaseEntry, dbName: string){
    if(!obj) {return console.warn('Please provide data to write')}
    
    try {
        // fs.writeFileSync('resultsDb.json',  JSON.stringify(obj));


        return console.log('Save successful');
    }
    catch(error) {
        console.error('Save failed: ' + error);
    }
}

export async function writeToDatabase(obj: DataBaseEntry){
    console.log(JSON.stringify(obj));
    const response = await fetch(url, {method: "post",  headers: {'Content-Type': 'application/json'}, body: JSON.stringify(obj), mode: 'no-cors'})

    if (!response.ok) console.log(await response.text);
}
