import React, {useState} from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import AddIcon from '@mui/icons-material/Add'

function App() {
  const [value, setValue] = useState("");

  function displayTotal() {
    console.log(`Number in the box is ${value}`);
  }

  return (
    <Paper elevation={5} sx={{width: "auto", height: "auto", display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '2%'}}>
      <Stack spacing={3} direction="row" >
        <TextField
              id="outlined-number"
              label="Number"
              type="number"
              slotProps={{
                inputLabel: {
                  shrink: true,
                },
              }}
              sx={{width: '25%'}}
              value={value}
              onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
                setValue(event.currentTarget.value);;
              }}
          />
        <AddIcon />
        <TextField
              id="outlined-number"
              label="Number"
              type="number"
              slotProps={{
                inputLabel: {
                  shrink: true,
                },
              }}
              sx={{width: '25%'}}
              value={value}
              onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
                setValue(event.currentTarget.value);;
              }}
          />
        <Button variant="contained" color="warning" onClick={displayTotal} disabled={value === ""}>Add</Button>
      </Stack>
    </Paper >
  );
}

export default App;
