import React, {useState} from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import AddIcon from '@mui/icons-material/Add'
import NumberField from './Components/NumberField';

function App() {
  type numberPair = {x: string; y: string};
  const [value, setValue] = useState<numberPair>({x: "", y:""});

  function displayTotal() {
    console.log(`Number in the left box is ${value.x}`);
    console.log(`Number in the right box is ${value.y}`);
  }

  const onNumberChange = (id: number, event: React.ChangeEvent<HTMLInputElement>) => {
    if(id === 0){
      setValue({x: event.currentTarget.value, y: value.y});
    } 
    else
    {
      setValue({x: value.x, y: event.currentTarget.value});
    }

  }

  return (
    <Paper elevation={5} sx={{width: "auto", height: "auto", display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '2%'}}>
      <Stack spacing={3} direction="row" >
        <NumberField changeEvent={0, onNumberChange}/>
        <AddIcon />
        <NumberField changeEvent={1, onNumberChange}/>
        <Button variant="contained" color="warning" onClick={displayTotal} disabled={value.x !== "" && value.y !== ""}>Add</Button>
      </Stack>
    </Paper >
  );
}

export default App;
