import React, {useState} from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import AddIcon from '@mui/icons-material/Add'
import NumberField from './Components/NumberField';

function App() {
  type numberPair = {x: string; y: string};
  const [value, setValue] = useState<numberPair>({x: "", y:""});

  function displayTotal() {
    console.log(`Number in the left box is ${value.x}`);
    console.log(`Number in the right box is ${value.y}`);
  }

  const onNumberChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setValue({x: event.currentTarget.value, y: "0"});
  }

  return (
    <Paper elevation={5} sx={{width: "auto", height: "auto", display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '2%'}}>
      <Stack spacing={3} direction="row" >
        <NumberField changeEvent={onNumberChange}/>
        <AddIcon />
        <NumberField changeEvent={onNumberChange}/>
        <Button variant="contained" color="warning" onClick={displayTotal} disabled={value.x === "" || value.y === ""}>Add</Button>
      </Stack>
    </Paper >
  );
}

export default App;
